import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import  ContexShop from './ContexShop';
//import { productsData } from '../data/ProductsData'
import { OpnionData } from '../data/OpnionData'

function ProviderShop({ children }) {
  // const [products, setProducts] = useState(productsData);

  const [opnion, setOpnion] = useState(OpnionData);
  const [messageContact, setMessageContact] = useState([]);
  const [email, setEmail] = useState([]);

  const [cartItens, setCartItens] = useState([]);
  const [cartAmout, setCartAmout] = useState(0);
  const [productsAmout, setProductsAmout] = useState({});
  const [cartTotal, setCartTotal] = useState(0);



  useEffect(() => {
    const amout = cartItens.reduce((previous, current) => (
      previous[current.id] ?
      {...previous, [current.id]: previous[current.id] += 1} :
      {...previous, [current.id]: 1}
    ),{});
    console.log(amout);

    setProductsAmout(amout);
    getTotal();
  }, [cartAmout]);

  function addToCart(info) {
    const { id } = info;

    setCartAmout(previous => previous + 1);
    console.log(cartAmout);

    setCartItens((previous) => [...previous, info]);
    console.log(cartItens);

    console.log(productsAmout);
    console.log(productsAmout[id]);

    // const check = cartItens.every((item) => item.id !== id);
      // console.log(check);
      // if(check){
      //   setCartItens((previous) => [...previous, info]);
      // } else {
      //   const amout = productsAmout.find((item) => item[id]);
      //   console.log(amout)
      //   amout[id] += 1;
      //   setProductsAmout((previous) => [...previous, { [id]: amout[id] },]);
      //   console.log(amout[id]);

      //   const filterRepeat = cartItens.filter((item) => item.id !== id);
      //   console.log(filterRepeat);
      //   setCartItens([...filterRepeat, info]);
      // }  
      // console.log(cartItens);
  }


  function increment(info) {
    const { id } = info;
    // const amout = productsAmout.find((item) => item[id]);
    // amout[id] += 1;
    // setProductsAmout((previous) => [...previous, { [id]: amout[id] }]);
    // setProductsAmout((previous) => {...previous, [id]: previous[id] += 1})
    setCartAmout(previous => previous + 1);

    console.log(productsAmout[id]);

    let amout = productsAmout[id];
    amout += 1;
    setProductsAmout(prevState => {
      return {...prevState, [id]: amout}
    });

  }

  function decrement(info) {
    const { id } = info;
    // const amout = productsAmout.find((item) => item[id]);

    // if(amout[id] === 1) {
    //   amout[id] = 1;
    //   setProductsAmout((previous) => [...previous,{ [id]: amout[id] }]);
    // } else {
    //   amout[id] -= 1 ;
    //   setProductsAmout((previous) => [...previous,{ [id]: amout[id] }]);
    //   setCartAmout(previous => previous - 1);
    // }
    if(productsAmout[id] === 1) {
      setProductsAmout(prevState => {
        return {...prevState, [id]: 1 }
      });
    } else {
      let amout = productsAmout[id];
      amout -= 1;
      setProductsAmout(prevState => {
        return {...prevState, [id]: amout }
      });
      setCartAmout(previous => previous - 1);
    }
  }

  function removeCart(info) {
    const { id } = info;
    // const amout = productsAmout.find((item) => item[id]);

    if(window.confirm("Do you want to delete this product?")){
      const filterRemove = cartItens.filter((item) => item.id !== id);
      setCartItens([...filterRemove]);
      setCartAmout(previous => previous - productsAmout[id]);
    }
  }

  function getTotal() {
    // if(cartItens.length !== 0){
    //   const total = cartItens.reduce((previous, product) => {
    //     const { id, price } = product;
    //     const amout = productsAmout.find((item) => item[id]);
    //     return previous + (Number(price) * Number(amout[id]));
    //   })
    //   console.log(total);
    //   setCartTotal(total);
    // }
  }

  const contextValue = {
    email,
    setEmail,
    opnion,
    setOpnion,
    messageContact,
    setMessageContact,
    addToCart,
    cartItens,
    cartAmout,
    cartTotal,
    productsAmout,
    increment,
    decrement,
    removeCart,
    setCartAmout,
    setProductsAmout,
  };

  return (
    <ContexShop.Provider value={ contextValue }>
      { children }
    </ContexShop.Provider>
  );
}

ProviderShop.propTypes = {
  children: PropTypes.node.isRequired,
};

export default ProviderShop;
