import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import ContextShop from '../contexApi/ContexShop';
import { AiOutlineShopping, AiOutlinePlus } from 'react-icons/ai';

function Product({info}) {
  const { img, name, price } = info;
  const { cartItens, setCartItens, cartAmout, setCartAmout, productAmout, setProductAmout } = useContext(ContextShop);

  function handleClick() {

    // if (cartItens.some((item) => item === name)) {
    //   return alert('this product is already in your cart');
    // }

    setCartAmout((previous) => previous + 1);
    console.log(cartAmout);

    const repeat = cartItens.some((item) => item.product.name === name);
    console.log(repeat);
    if(repeat){
      setProductAmout((previous) => previous + 1);
      console.log(productAmout);

      const filterRepeat = cartItens.filter((item) => item.product.name !== name);
      setCartItens([
        filterRepeat, 
        {
          product: info,  
          productAmout,
        },
      ]);
    }

    // }

    // setCartAmout((previous) => previous + 1);
    // console.log(cartAmout);

    setCartItens((previous) => [
      ...previous, 
      {
        product: info,  
        productAmout,
      },
    ]);

    console.log(cartItens);
  }

  return(
    <div>
      <img src={ img } alt="product img" width="100"/>
      <h4> { name } </h4>
      <p> { price }</p>

      <Link to={ `/movies/${name}` }>
        <button type="button">
          quero conhecer
        </button>
      </Link>

      <button type="button" onClick={ handleClick }>
        <AiOutlinePlus /> <AiOutlineShopping />
      </button>
    </div>
  );
  }

  export default Product;
