        productAmout,
      },
    ]);

    console.log(cartItens);

  }

  const contextValue = {
    email,
    setEmail,
    addToCart,
  };

  return (
    <ContexShop.Provider value={contextValue}>
      {children}
    </ContexShop.Provider>
  );
}

ProviderShop.propTypes = {
  children: PropTypes.node.isRequired,
};

